mapper.select:
select * from activity_detail

	 and activity_type_id = #{activityTypeId} 
	 and activity_rule_id = #{activityRuleId} 
	 and activity_prize_type_id = #{activityPrizeTypeId} 
	 and activity_setting_id = #{activitySettingId} 
	 and budget = #{budget} 
	 and used_budget = #{usedBudget} 
	 and activity_times = #{activityTimes} 
	 and activity_use_times = #{activityUseTimes} 
	 and is_on = #{isOn} 
	 and state = #{state}

mapper.update:
update activity_detail
activity_type_id = #{activityTypeId} , activity_rule_id = #{activityRuleId} , activity_prize_type_id = #{activityPrizeTypeId} , activity_setting_id = #{activitySettingId} , budget = #{budget} , used_budget = #{usedBudget} , activity_times = #{activityTimes} , activity_use_times = #{activityUseTimes} , is_on = #{isOn} , state = #{state} ,
where id = #{id}
